import React, { Component } from "react";
import { Link } from "react-router-dom";
import Card from "../Components/Card.js";
import "../App.css"
import Modal from "bootstrap";

class Home extends Component {

  componentDidMount(){
        let visited = localStorage["alreadyVisited"];
        if(visited) {
             this.setState({ viewPopup: false })
             //do not view Popup
        } else {
             //this is the first time
             localStorage["alreadyVisited"] = true;
             this.setState({ viewPopup: true});
        }
  }
  render() {
    return (
        <div>

        <div class="modal fade" id="exampleModalLong" tabindex="-1" role="dialog" aria-labelledby="exampleModalLongTitle" aria-hidden="true">
  <div class="modal-dialog" role="document">
    <div class="modal-content">
      <div class="modal-header">
        <h5 class="modal-title" id="exampleModalLongTitle">Modal title</h5>
        <button type="button" class="close" data-dismiss="modal" aria-label="Close">
          <span aria-hidden="true">&times;</span>
        </button>
      </div>
      <div class="modal-body">
        ...
      </div>
      <div class="modal-footer">
        <button type="button" class="btn btn-secondary" data-dismiss="modal">Close</button>
        <button type="button" class="btn btn-primary">Save changes</button>
      </div>
    </div>
  </div>
</div>

          <div className="container-fluid" id="container-scroll">
            <div className="row">

              <div className="col-sm-6 col-lg-3">
                { /* insert navigation in this column */ }

              </div>

              <div className="col-sm-6 col-lg-3">
                <Link to="/Books" className="noTextDecoration">
                  <Card title="Books" message="Buy and Sell Books" />
                </Link>
              </div>

              <div className="col-sm-12 col-lg-3">
                <Link to="/Notes" className="noTextDecoration">
                  <Card title="Notes" message="Share Notes" />
                </Link>
              </div>

              <div className="col-sm-12 col-lg-3">
                <Link to="/RideSharing" className="noTextDecoration">
                  <Card title="Ridesharing" message="Ride Together" />
                </Link>
              </div>

            </div>
          </div>
        </div>
    );
  }
}

export default Home;
